#customized
stop_words += ['singapore', 'bali']

files = []

def walk(rootDir):
    for lists in os.listdir(rootDir):
        path = os.path.join(rootDir, lists)
        if '.xls' in path:
            files.append(path)
        if os.path.isdir(path):
            walk(path)
    return files


def filter_frequency(filename, fre):
    alldata = []
    data = xlrd.open_workbook(filename)
    table = data.sheets()[0]
    for i in range(1, table.nrows):
        frequency = int(table.row(i)[1].value)
        if frequency >= fre:
            alldata.append([table.row(i)[0].value, table.row(i)[1].value])
    return alldata


def write_excel(filename, alldata):
    d = os.path.dirname(filename)
    if not os.path.exists(d):
        os.makedirs(d)
    w = xlwt.Workbook(encoding='utf-8')
    ws = w.add_sheet('old', cell_overwrite_ok=True)
    row_length = len(alldata)
    if row_length > 1000:
        row_length = 1000
    for row in range(0,row_length):
        one_row = alldata[row]
        for col in range(0, len(one_row)):
            ws.write(row, col, one_row[col])
    w.save(filename)


def load_data_from_excel(filename, start):
    alldata = [['words', 'frequency']]
    data = xlrd.open_workbook(filename)
    table = data.sheets()[0]
    for i in range(start, table.nrows):
        try:
            is_in = False
            data = table.row(i)[0].value
            temp_data = data.split(',')
            for en in temp_data:
                entry = str(en.replace(' ', ''))
                if entry.lower().strip() in stop_words or entry.strip() == '' or entry.strip().isdigit():
                    is_in = True
                    break
            if is_in is False:
                alldata.append([table.row(i)[0].value, table.row(i)[1].value])
        except:
            continue

    return alldata

file_list = walk('ngram_resultdata')

for files in file_list:
    try:
        alldata = load_data_from_excel(files, 0)
        write_excel(files.replace('ngram_', 'filtered_'), alldata)
        del alldata
        gc.collect()
        print("SUCCESS==="+files)
    except:
        print("ERROR====="+files)
